import React,{useEffect,useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useParams } from 'react-router-dom';
import { Grid, Divider, Card, CardContent } from '@material-ui/core';
import services from '../../services/services';
import AdminLink from '../management/AdminLinks';


const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    gridContainer: {

      paddingTop:"50px",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
    headPart:{
        backgroundColor:"#133b5c",
      color:"#ffff"
    },
    icon:{
        color:"#fff"
    },
    heading: {
      fontSize: theme.typography.pxToRem(20),
      fontWeight: theme.typography.fontWeightRegular,
    },
  }));

  export default function AllQuestionList() {
    const classes = useStyles();

    const {id} = useParams();
    const [questionList, setQuestionList] = useState([]);
    useEffect(() => {
        getQuestionlist();
    }, [])

    const getQuestionlist = async () => {
        const data={
             categoryId:id
        }
        const options = {
            method: 'GET',
            headers: {
                "Apiuserid": "49507884",
                "Scope": "In.QuizApp.READ",
            }
        }
        const result = await services.getQuestionlist(data,options)
        console.log(result)
        setQuestionList(result.data.data)
    };

    return (
      <div className={classes.root}>
        <AdminLink/>
          <Grid
                container
                className={classes.gridContainer}
                justify="center"
             >
               {
                 questionList.map((list,index)=>{
                   return(
                    <Grid container key={index}>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} >
                      <Card className="questioncard">
                   <h3 className="questionname">{list.question}</h3>
                         <div className="optioncard">
                         {
                           list.optionsArray.map((option,index)=>{
                                return(
                                  <Grid
                                  container
                                  className="nonclickoption"
                                  key={index}
                                 >
                                <Grid item xs={4} sm={4} md={4} lg={4} >
                                     <img className="optionImg" src={option.optionImg} /> 
                                </Grid>
                                <Grid item xs={8} sm={8} md={8} lg={8} className="optionbg" >

                                 <CardContent className="cardcontent">
                                   <h3 className="optioncontent" id="cardText">
                                    {option.optionLabel}
                                   </h3>
                                 </CardContent>

                               </Grid>
                               </Grid>
                                )
                           })
                         }


                        </div>

                      </Card>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                   )
                 })
               }

         </Grid>
        </div>
  );
}
