import React from 'react'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import '../style/questionlist.css'
import LinearProgress from '@material-ui/core/LinearProgress';


const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
      width:"100%",
    },
    colorPrimary: {
      backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
    },
    bar: {
      borderRadius: 5,
      backgroundColor: '#FFDD52',
    },
  }))(LinearProgress);

const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      pro:{
          textAlign:"center"
      }

  }));
  function QuestionList({questionlist}) {

    const classes = useStyles();

    let tutorialSteps = [
      {
          label: questionlist?.question,
          options: questionlist?.optionsArray,
      },
  ];


if (questionlist?.length) {
  tutorialSteps = questionlist;
}
const [activeStep, setActiveStep] = React.useState(0);
const maxSteps = tutorialSteps.length;
const handleNext = () => { 

  setActiveStep(prevActiveStep => prevActiveStep + 1); };

const handleSkip = () => { setActiveStep(prevActiveStep => prevActiveStep + 1); };
const handleSubmit = () => {
   var label=[];
  var label=document.getElementById("cardText").textContent
  debugger
 };
      return (
          <div className={classes.gridContainer}>
            <Grid container>
               <Grid item xs></Grid>
                <Grid item xs={11} sm={6} md={6} lg={4} >
                     <div justify="center">
                        <h3 className="text1">2021 Friendship</h3>
                        <h3 className="text2">Dare</h3>
                     </div>
                     <div justify="center">     
                        <h4 className="question">Question <span>1/15</span></h4>
                     </div>
                     <div justify="center">
                        <BorderLinearProgress variant="determinate" value={50} />
                     </div>
                <Card className="questioncard">
      <h3 className="questionname">{tutorialSteps[activeStep]?.qusId}: {tutorialSteps[activeStep]?.question}</h3>


                        <div className="carddesign">
{
  tutorialSteps[activeStep]?.optionsArray?.map((options,index)=>{
    return(<Grid
      container
      className="optionpart"
      onClick={handleNext}
      key={index}
      >
       <Grid item xs={4} sm={4} md={4}lg={4} id={options.optionId} >

           {options.optionImg.length
             ?<img className="optionImg" 
              src={options.optionImg}/>

             :<span></span>

           }
      </Grid>
      <Grid item xs={8} sm={8} md={8}lg={8} className="optionbg" >
        <CardContent className="cardcontent">
          <h3 className="optioncontent" id="cardText">
          {options.optionLabel}
          </h3>
        </CardContent>
      </Grid>
      </Grid>)

  })
}
                    </div>

                    <div className="start-wrapper">

                     {

                     maxSteps-1 !=tutorialSteps[activeStep].question.length?  <button className="skip"
                       onClick={handleSkip}
                       >Skip the Question</button>:
                       <button className="skip"onClick={handleSubmit}
                       >Submit</button>
                     }
                    </div> 
             </Card>
                </Grid>
                <Grid item xs></Grid>
           </Grid>
      </div>
    )
}

export default QuestionList
