import React, { useState } from 'react'
import '../style/score.css'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ReactSpeedometer from "react-d3-speedometer"



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function Score(props) {
  console.log(props)
  const score=props.location.state.data.score;
  const list=props.location.state.quizlist.quizlist.length;
  const [speedo,setSpeedo]=useState(0)

      // var per=(score/list)*100
      // setSpeedo(per)

const classes = useStyles();
return (
<>
<div className={classes.gridContainer}>
<Grid container>
<Grid item xs>

</Grid>
<Grid item xs={11} sm={6} md={6} lg={4} >

<div justify="center" className={classes.head}>
<h3 className="text1">2021 Friendship</h3>
<h3 className="text2">Dare</h3>
</div>
<Card className="score-card">
<h3 className="how">Your Score:   {score}/{list}</h3>
  <Grid container justify="center">  
  <ReactSpeedometer
    value={speedo}
    className="speedometer"
    segments={4}
    segmentColors={["#FF3300","#FFB605","#00B71D","#048E15"]}
    needleColor={"#707070"}
    currentValueText="Happiness Level"
    customSegmentLabels={[
      {
        text: "Very Bad",
        position: "INSIDE",
        color:"#fff",
        fontSize: "16px",

      },
      {
        text: "Bad",
        position: "INSIDE",
        color: "#fff",
        fontSize: "16px",
      },
      {
        text: "Ok",
        position: "INSIDE",
        color: "#fff",
        fontSize: "16px",
      },
      {
        text: "Good",
        position: "INSIDE",
        color: "#fff",
        fontSize: "16px",
      },

    ]}
  /> 
  </Grid>
     <button className="copy">Create Your Quiz</button>  

      <div className="sharelink">
                         <h3 className="challenge">Who knows you the best:</h3>
                       </div>
                      <Grid container justify="center">                   
                            <table className="table-data">
                                <tr>
                                    <th>Name</th>
                                    <th>Score</th>
                                </tr>
                                <tr>
                                    <td>January</td>
                                    <td>$100</td>
                                </tr>
                            </table>                   
                      </Grid>              
</Card>

</Grid>
<Grid item xs>

</Grid>

</Grid>
<br/>
<Grid container>
<Grid item xs>

</Grid>
<Grid item xs={11} sm={6} md={6} lg={4}>

</Grid>
<Grid item xs>

</Grid>
</Grid>

</div>
</>
);
}

export default Score
