import React, { useState } from 'react'
import { Grid, Card, CardContent } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import '../style/questionlist.css'
import LinearProgress from '@material-ui/core/LinearProgress';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';
import Footer from '../footer/Footer';


const BorderLinearProgress = withStyles((theme) => ({
  root: {
    height: 10,
    borderRadius: 5,
    width: "100%",
  },
  colorPrimary: {
    backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
  },
  bar: {
    borderRadius: 5,
    backgroundColor: '#FFDD52',
  },
}))(LinearProgress);

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    paddingLeft: "20px",
    paddingRight: "20px",
    background: "#7555D9",
    minHeight: "100vh"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  pro: {
    textAlign: "center"
  }

}));
function QuestionList({ questionlist }) {

  const classes = useStyles();
  console.log(questionlist)
  let tutorialSteps = [
    {
      label: questionlist?.question,
      options: questionlist?.optionsArray,
    },
  ];


  if (questionlist?.length) {
    tutorialSteps = questionlist;
  }
  const data = JSON.parse(localStorage.getItem('userInfo'));
  const createdBy = data['name'];
  const [activeStep, setActiveStep] = React.useState(0);
  const [answerList, setAnswerList] = React.useState([]);
  const [createQuiz, setCreateQuiz] = useState();
  const [result, setResult] = useState(false);
  const [progress, setProgress] = useState(0);
  const [count, setCount] = useState(1);
  const [clickedAns, setClickedAns] = useState(0);
  const maxSteps = tutorialSteps.length;

  function showProgress(index) {

    var increment = Math.ceil((activeStep) / (maxSteps) * 100);

    setProgress(increment)

  }


  const handleNext = (id, label) => {
    const qusId = tutorialSteps[activeStep]?.qusId;
    const answer = label;
    const answerId = id;
    setClickedAns(label)

    if (maxSteps - 1 === activeStep) {
      setAnswerList(
        [...answerList, { qusId: qusId, answer: answer, answerId: answerId }]
      )


      setTimeout(() => { handleSubmit([...answerList, { qusId: qusId, answer: answer, answerId: answerId }]) }, 500)



    }
    else {
      setAnswerList(
        [...answerList, { qusId: qusId, answer: answer, answerId: answerId }]
      )
      console.log();
      setTimeout(() => {
        setActiveStep(prevActiveStep => prevActiveStep + 1)
        setCount(count + 1)
      }, 500)


      // debugger
    }
    showProgress(tutorialSteps[activeStep].qusId);




  }


  const handleSkip = () => { setActiveStep(prevActiveStep => prevActiveStep + 1); };

  const handleSubmit = async ([...answerList]) => {


    const list = {
      createdBy: createdBy,
      quizQuestions: answerList
    }
    console.log(list)
    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
      }
    }
    const result = await services.createQuiz(list, options)
    setCreateQuiz(result.data.data)
    setResult(true)
    if (result.data.code === 200) {
      setResult(true)
    }

  };

  if (result) {
    localStorage.setItem("createId", JSON.stringify(createQuiz))
    return (<Redirect to={{ pathname: "/sharelink", state: { quizId: createQuiz } }} push={true} />)
  }

  return (
    <div className={classes.gridContainer}>
      <Grid container>
        <Grid item xs></Grid>
        <Grid item xs={11} sm={6} md={6} lg={4} >
          <div justify="center">
            <h3 className="text1">2021 Friendship</h3>
            <h3 className="text2">Dare</h3>
          </div>
          <div justify="center">
            <h4 className="question">Question <span>{count}/{maxSteps}</span></h4>
          </div>
          <div justify="center">
            <BorderLinearProgress variant="determinate" value={progress} />
          </div>
          <Card className="questioncard">
            <h3 className="questionname">{count}: {tutorialSteps[activeStep]?.question}</h3>
            <div className="carddesign">
              {
                tutorialSteps[activeStep]?.optionsArray?.map((options, index) => {
                  return (
                    <Grid
                      container
                      className={
                        clickedAns === options.optionLabel ?
                          'correct' : 'optionpart'}
                      onClick={() => { handleNext(options.optionId, options.optionLabel) }}
                      key={index}
                      id="cardbox">
                      <Grid item xs={4} sm={4} md={4} lg={4} >
                        {options.optionImg.length ?
                          <img className="optionImg" src={options.optionImg} /> : <span></span>
                        }
                      </Grid>
                      <Grid item xs={8} sm={8} md={8} lg={8} className="optionbg" >
                        <CardContent className="cardcontent">
                          <h3 className="optioncontent" id="cardText">
                            {options.optionLabel}
                          </h3>
                        </CardContent>
                      </Grid>
                    </Grid>
                  )
                })
              }
            </div>
            <div className="start-wrapper">
              <button className="skip" onClick={() => { maxSteps - 1 != tutorialSteps[activeStep].question.length ? handleSkip() : handleSubmit() }}>Skip the Question</button>
            </div>
          </Card>
        </Grid>
        <Grid item xs></Grid>
      </Grid>
      <Footer />
    </div>
  )
}

export default QuestionList
