import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import '../style/questionlist.css'
import LinearProgress from '@material-ui/core/LinearProgress';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
      width:"100%",
    },
    colorPrimary: {
      backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
    },
    bar: {
      borderRadius: 5,
      backgroundColor: '#FFDD52',
    },
  }))(LinearProgress);

const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      pro:{
          textAlign:"center"
      }

  }));
function PlayQuiz({quizlist}) {
  console.log(quizlist)
  let quiz = [
    {
        label: quizlist?.question,
        options: quizlist?.options,
    },
];

    const classes = useStyles();
    if (quizlist?.length) {
      quiz = quizlist;
    }

    const [activeStep, setActiveStep] = React.useState(0);
    const [answerList, setAnswerList] = React.useState([
      {
        qusId: '',
        answer: '',
         answerId:''
      }
    ]);

    const maxSteps = quiz.length;
    const handleNext = (id,label) => { 
      if( maxSteps-1 !=quiz[activeStep].question.length){
        setActiveStep(prevActiveStep => prevActiveStep + 1);
      }

      const qusId=quiz[activeStep]?.qusId;
      const answer=label;
      const answerId=id;
      setAnswerList(
       [ ...answerList,{qusId:qusId,answer:answer,answerId:answerId}]
      )

    };

    const handleSkip = () => { setActiveStep(prevActiveStep => prevActiveStep + 1); };

    const handleSubmit = async() => {
    //   var indexToRemove = 0;
    //   var numberToRemove = 1;
    //  answerList.splice(indexToRemove, numberToRemove);

    //  const list={
    //      createdBy:createdBy,
    //      quizQuestions:answerList
    //  }
    //  const options = {
    //   method: 'POST',
    //   headers: {
    //     "Apiuserid": "49507884",
    //     "Scope": "In.QuizApp.INSERT",
    //   }
    // }
    // const result = await services.createQuiz(list, options)
    //  setCreateQuiz(result.data.data)
    //  console.log(result)
    //  setResult(true)
    //  if(result.data.code===200)
    //  {
    //   setResult(true)
    //  }

     };

    return (
        <div className={classes.gridContainer}>
        <Grid container>
           <Grid item xs></Grid>
            <Grid item xs={11} sm={6} md={6} lg={4} >
                 <div justify="center">
                    <h3 className="text1">2021 Friendship</h3>
                    <h3 className="text2">Dare</h3>
                 </div>
                 <div justify="center">     
                    <h4 className="question">Question <span>1/15</span></h4>
                 </div>
                 <div justify="center">
                    <BorderLinearProgress variant="determinate" value={50} />
                 </div>
            <Card className="questioncard">
  <h3 className="questionname">What is Your name</h3>


                    <div className="carddesign">
{
quiz[activeStep]?.options?.map((options,index)=>{

return(
    <Grid
  container
  className="optionpart"
  onClick={()=>{handleNext(options.optionId,options.optionLabel)}}
  key={index}
  >
   <Grid item xs={4} sm={4} md={4}lg={4}>

       <img className="optionImg" 
          src={options.optionImg}/>


  </Grid>
  <Grid item xs={8} sm={8} md={8}lg={8} className="optionbg" >
    <CardContent className="cardcontent">
      <h3 className="optioncontent" id="cardText">
     {options.optionLabel}
      </h3>
    </CardContent>
  </Grid>
  </Grid>
 )

})
} 
                </div>

                <div className="start-wrapper">

                 {

                 maxSteps-1 !=quiz[activeStep].question?  <button className="skip"
                   onClick={handleSkip}
                   >Skip the Question</button>:
                   <button className="skip"onClick={handleSubmit}
                   >Submit</button>
                 }
                </div> 
         </Card>
            </Grid>
            <Grid item xs></Grid>
       </Grid>
  </div>
    )
}

export default PlayQuiz
