import React,{useState,useRef, useEffect} from 'react'
import '../style/sharelink.css'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import services from '../../services/services';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function ShareLink(props) {

    const classes = useStyles();
    // const id=props.location.state.quizId.quizId
    const data = JSON.parse(localStorage.getItem('id'));
    const id = data['quizId'];
    const textAreaRef = useRef(null);

    function copyToClipboard(e) {
    textAreaRef.current.select();
    document.execCommand('copy');
    e.target.focus();

    };

   useEffect(() => {
       getCreaterResult();
   }, [])

   const getCreaterResult = async()=>{
   const dataid = {
    quizId: id,
  }
  const options = {
    method: 'POST',
    headers: {
      "Apiuserid": "49507884",
      "Scope": "In.QuizApp.Read",
    }
  }
  const result = await services.getCreaterResult(dataid, options)
  console.log(result)
   }
        return (
        <>
        <div className={classes.gridContainer}>
            <Grid container>
                <Grid item xs></Grid>
                <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div justify="center" className={classes.head}>
                        <h3 className="text1">2021 Friendship</h3>
                        <h3 className="text2">Dare</h3>
                    </div>

                    <Card className="form-card">
                        <div className="sharelink">
                            <h3 className="challenge">Your Challenge is ready</h3>
                            <img src="/assets/image/friend.svg"/>
                            <h3 className="this-link">Share this link with your friends</h3>
                            <input  ref={textAreaRef} className="quizInput" defaultValue={`http://localhost:3002/friendlink/${id}`} />

                            <br />
                        </div>
                        <div className="btn-wrapper">
                            <button  onClick={copyToClipboard} className="copy"> Copy Link</button>
                        </div>
                        <div className="btn-wrapper">
                            <button className="whatsapp">
                                <a href="#">
                                  <img src="/assets/image/whatsapp.png" className="whatsapp-img"/><span className="status">WhatsApp Status</span> 
                                </a>
                            </button>                            
                            <button className="instagram">
                                <a href="#">
                                  <img src="/assets/image/instagram.png" className="instagram-img" /><span className="instagram-status">Add to Instagram Bio</span>
                                </a>
                            </button> 
                           <button className="sharing">
                                <a href="#">
                                  <img src="/assets/image/sharing.svg"className="sharing-img" /><span className="status">Share</span>
                                </a>
                          </button>       
                       </div>
                       <div className="wrapper">
                          <button className="result"> View Results</button>
                       </div>                   
                       <div className="sharelink">
                         <h3 className="challenge">Who knows you the best:</h3>
                       </div>
                      <Grid container justify="center">                   
                            <table className="table-data">
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Score</th>
                                    </tr>
                                </thead>
                                <tbody>
                                <tr>
                                    <td>January</td>
                                    <td>$100</td>
                                </tr>
                                </tbody>
                            </table>                   
                      </Grid>
                     <Grid item xs> </Grid>
                    </Card>
                </Grid>
                <Grid item xs>

                </Grid>
            </Grid>
            <br/>
        </div>
        </>
        );
}

export default ShareLink
