import React, { useState,useEffect } from 'react';
import '../style/form.css';
import {  Grid, Card} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Redirect } from 'react-router-dom';
import services from '../../services/services';
import Footer from '../footer/Footer';

const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      list:{
        margin:"25px auto",
        textAlign:"center"
    },
    add:{
      width:"100%"
  }

  }));

 function Form() {
    const classes = useStyles();
    const [data,setData]=useState(false);
    const [question,setQuestion]=useState([])

    var categoryId = JSON.parse(localStorage.getItem("categoryId"));

    const handleValid = (e)=>{
      var  name=e.target.value;
      if(name == "") {
        alert("please enter your name")
    } else {
        var regex = /^[a-zA-Z\s]+$/;                
        if(regex.test(name) === false) {
            console.log("nameErr", "Please enter a valid name");
        } else {
           console.log("nameErr", "");

        }
    }

    }

    const handleSubmit =()=>{
      var  selectLanguage= document.getElementById("selectLanguage").value;
      var  name=document.getElementById("name").value;
      var data={
          selectLanguage:selectLanguage,
          name:name
           }
        localStorage.setItem("userInfo",JSON.stringify(data));
        setData(true);
       }

       useEffect(() => {
           getQuestionlist();
       }, [1])

       const getQuestionlist = async () => {
           const data={
               categoryId:categoryId
           }
           console.log(data)
           const options = {
               method: 'POST',
               headers: {
                   "Apiuserid": "49507884",
                   "Scope": "In.QuizApp.READ",   
               }
           }
           const result = await services.getQuestionlist(data,options)
           console.log(result)
           setQuestion(result.data.data)
       };
     if(data) return(<Redirect to="/questionlist" />)
return (
<>
    <div className={classes.gridContainer}>
        <Grid container>
          <Grid item xs>

          </Grid>
          <Grid item xs={11} sm={6} md={6} lg={4} className="form-grid" >
                <div justify="center" className={classes.head}>
                    <h3 className="text1">2021 Friendship</h3>
                    <h3 className="text2">Dare</h3>
                </div>
                <Card className="form-card">
                  <form>
                    <h3 className="language">Select Language:</h3>
                    <select id="selectLanguage">
                        <option defaultValue>Select Language</option>
                        <option value="English">English</option>

                    </select>
                    <h3 className="language">Name</h3>
                      <input type="text" id="name" className="name" placeholder="Name..." onChange={handleValid}required /><br />
                    <div className="start-wrapper">
                      <button className="started" onClick={handleSubmit}> Start</button>
                    </div>
                  </form>
              </Card>
          </Grid>
          <Grid item xs></Grid>
       </Grid>
       <br/>
       <Grid container>
                    <Grid item xs></Grid>
                    <Grid item className={classes.list} xs={11} sm={6} md={6} lg={4}>
                        <a href="#">
                           <img src="/assets/image/Image5.png" className={classes.add}/>
                        </a>
                    </Grid>
                    <Grid item xs></Grid>
                </Grid>
      {
            question?.map((question,index)=>{
              return( <Grid container justify="center">

                         <Grid className="paragrid"item xs={11} sm={6} md={6} lg={4}>
                          <p className="question-para"><span>{question.qusId})</span>{question.question}</p> </Grid>

      </Grid>
       )
       })
      }
       <Footer/>
    </div>

</>
);
}

export default Form
