import React,{useState,useEffect} from 'react'
import '../style/friendlink.css'
import {  Grid,Card} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useParams, Link } from 'react-router-dom';
import services from '../../services/services';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      head:{
          textAlign:"center"
      },
      list:{
          marginTop:"30px"
      }


  }));

function FriendLink() {
const classes = useStyles();
const [quiz,setQuiz]=useState([])
const [list,setList]=useState([]) 
const [question,setQuestion]=useState([])

    var categoryId = JSON.parse(localStorage.getItem("categoryId"));  
   const { id } = useParams()
   useEffect(() => {
       getQuiz();
   }, [])

   const getQuiz = async () => {
       const data={
            quizId:id
       }

       const options = {
           method: 'GET',
           headers: {
               "Apiuserid": "49507884",
               "Scope": "In.QuizApp.READ",

           }
       }
       const result = await services.getQuiz(data,options)
       setQuiz(result.data.data)
       setList(result.data.data.quizQuestions)

    };
    useEffect(() => {
        getQuestionlist();
    }, [1])

    const getQuestionlist = async () => {
        const data={
            categoryId:categoryId
        }
        console.log(data)
        const options = {
            method: 'POST',
            headers: {
                "Apiuserid": "49507884",
                "Scope": "In.QuizApp.READ",   
            }
        }
        const result = await services.getQuestionlist(data,options)
        console.log(result)
        setQuestion(result.data.data)
    };
 const playby=()=>{
     var name=document.getElementById("feedbackBy").value;
     localStorage.setItem("feedbackby",name);
 }
    return (
        <>
            <div className={classes.gridContainer}>
                <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4}>                    
                        <div justify="center" className={classes.head}>
                            <h3 className="friendship">2021 Friendship</h3>
                            <h3 className="dare">Dare</h3>
                        </div>
                        <Card className="form-card">
                           <h3 className="how">How well do you know {quiz.createdBy}?</h3>
                            <form >

                                 <h4 className="language">First Name</h4>
                                <input type="text" id="feedbackBy" className="name" placeholder="FirstName..." onChange={playby} /><br />
                                <Link  to={{
                                        pathname: '/quizlink',
                                        state:{quizQuestions:quiz}}}>
                                <div className="start-wrapper">
                                    <button className="started"> Start</button>
                                </div>
                                </Link>
                            </form>
                            <h3 className="how">Who Knows {quiz.createdBy} Best ?</h3>
                        </Card>
                    </Grid>
                  <Grid item xs></Grid>

                </Grid>
                <br/>
                <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4}>
                        <a href="#">
                           <img src="/assets/images/Image5.png"/>
                        </a>
                    </Grid>
                    <Grid item xs></Grid>
                </Grid>
            <Grid container>
            <Grid item xs></Grid>
             <Grid container className={classes.list} justify="center">
             {
            question?.map((question,index)=>{
              return(
                         <Grid className="paragrid"item xs={11} sm={6} md={6} lg={4}>
                          <p className="question-para"><span>{question.qusId})</span>{question.question}</p> </Grid>
                          )
                        })
                       }

      </Grid>

            <Grid item xs></Grid>
            </Grid>
            </div>
        </>
    );
    }

export default FriendLink
