import React, { useState } from 'react';
import { Container, Row, Col, Form } from 'react-bootstrap';
import "./Login.css"
import services from '../../services/services';
import { Redirect } from 'react-router-dom';

const Login = () => {
const[loggedIn,setLoggedIn]=useState(false)
const[page,setPage]=useState([])

const login = async() => {
    var username = document.getElementById("username").value;
    var password = document.getElementById("password").value;

    var data = {
     username: username,
      password: password,
    }
    const options = {
      method: 'POST',
     headers:{
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.LOGIN"
     }
    }
      const result= await services.login(data,options)
      console.log(result)
     if(result.data.code==400)
     {

         console.log("error")
     }
     else{
        setPage(result.data)
        setLoggedIn(true)
     }
  }  

  localStorage.setItem('userData', JSON.stringify(page)); 
  if(loggedIn)
  {
      return(<Redirect to="/usermanagement"/>)
  }
    return (
        <>
          <Container fluid className="wrapper fadeInDown">
                <Row>
                    <Col className="formContent mt-5" sm={12} md={6} lg={4}>
                        <div className="fadeIn first">
                            <img src="/assets/image/download.png" id="icon" alt="user icon" height="90" className="mb-3" />
                        </div>
                            <input  type="text" id="username" className="fadeIn Second mb-4"
                            name="username"
                            placeholder="User Name"
                            />

                            <input  type="text" id="password" className="fadeIn third
                            mb-4"
                            name="password"
                            placeholder="Password"
                            />

                            <input  type="submit" className="btn btn-primary btn-block fadeIn fourth mb-3"
                            onClick={login}
                            />
                    </Col> 
                </Row>     
          </Container>  
        </>
    )
}

export default Login;
