import '../style/score.css';
import '../style/quizscore.css';
import {  Grid, Card} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ReactSpeedometer from "react-d3-speedometer";
import { Link } from 'react-router-dom';
import Footer from '../footer/Footer';
import services from '../../services/services';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function QuizScore(props) {
    const score=props.location.state.score.score;
    const question=props.location.state.quizLength.maxSteps;
    const name=props.location.state.name.name;
    const allresult=props.location.state.resultData.allresult;

