//      text ="Very Bad Friend"
//   }

  const classes = useStyles();
    return (
    <>
    <div className={classes.gridContainer}>
    <Grid container>
            <Grid item xs></Grid>
            <Grid item xs={11} sm={6} md={4} lg={3} >
                <Card className="player-list">
                    <Grid container justify="center">
    <span className="pname" key="" >{name}</span>
                </Grid>
                </Card>
            </Grid>
           <Grid item xs></Grid>
       </Grid>
    <Grid container>
       <Grid item xs></Grid>
      <Grid item xs={11} sm={6} md={4} lg={3} >
          <Card className="score-card">
    <span className="tagline">Mahan to ap hai</span>
            <Grid container justify="center">  
                <ReactSpeedometer
                    maxValue=""
                    value=""
                    minValue={0}
                    className="speedometer"
                    segments={5}
                    ringWidth={60}
                    needleHeightRatio={.5}
                    needleTransition={"easeBackInOut"}
                    segmentColors={["#fff"," #fff"," #fff"," #fff"]}
                    needleColor={"#707070"}
                    customSegmentLabels={[
                      {
                        text: "😡",
                        position: "INSIDE",
                        color:"#fff",
                        fontSize:"35px",



                      },
                      {
                        text: "😞",
