import React,{useState,useEffect} from 'react';
import '../style/category.css';
import { makeStyles, Grid, Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';
import Footer from '../footer/Footer';
import { useCookies } from 'react-cookie';

    const useStyles = makeStyles({
        gridContainer: {
            background:"#7555D9",
            minHeight:"100vh",
            padding:"0px 20px"

        },
        grid:{
            marginBottom:"20px"
        },


    });

function QuizCategory() {
    const classes = useStyles();
    const[category,setCategory] = useState([]);
    const[page,setPage] = useState(false);
    const [id,setId]= useState(0)
    const [cookies, setCookie] = useCookies(['playQuizId']);
    useEffect(() => {
        getAllAdminQuiz();

    }, [])

    const  getAllAdminQuiz = async () => {
        const options = {
        method: 'Get',
        }
    const result = await services.getAllAdminQuiz(options)
     console.log(result)
      setCategory(result.data.data)
    };

   const handleClick =(id,title,image)=>{
    localStorage.setItem('playQuizId', JSON.stringify(id)); 
    setId(id)
    setPage(true);
    debugger
    setCookie('playQuizId', id, { path: '/quizCategory' });
    return <Redirect to={{pathname:"/quizform",state:{id:{id},title:{title},image:{image}}}} push={true}/>
    }

  //return <Redirect to={{pathname:"/form",state:{id:{id}}}} push={true}/>




        return (
            <>{
                category.length? 
                <Grid container className={classes.gridContainer} justify="center">
                <Grid item xs={12} sm={6} md={3}>
                <h3 className="choose1">Choose your</h3>
                <h3 className="choose2">Category</h3>
                </Grid>
                {
                  category.map((list,index)=>{
                 return(  
                       <>
                       {
                           list.bannerImage!=""?
                           <Grid container justify="center" className={classes.grid} key={index}>
                           <Grid item xs={12} sm={7} md={6}lg={3} >
                               <Card className="card" key={index} onClick={()=>{handleClick(list.adminQuizId,list.bannerImage,list.title)}} >
                                   <div>  
                                       <Grid container>
                                           <Grid item xs={4} sm={4} md={3}lg={3} className="img-card" >
                                               <img className="cardImg" src={list.bannerImage}/>
                                           </Grid>
                                           <Grid item xs={8} sm={8} md={9}lg={9} className="content-bg" >
                                               <CardContent className="card-category">
                                                   <h3 className="content">{list.title}</h3>
                                               </CardContent>
                                           </Grid>
                                       </Grid>
                                   </div>
                               </Card>
                           </Grid>
                       </Grid>:
                      <Grid container justify="center" className={classes.grid} key={index}>
                      <Grid item xs={12} sm={7} md={6}lg={3} >
                              <Card className="card" key={index} onClick={()=>{handleClick(list.adminQuizId)}} >
                                  <div>  
                                      <Grid container>
                                          <Grid item xs={12} sm={12} md={12}lg={12} className="content-bg" >
                                              <CardContent className="card-category">
                                                  <h3 className="content">{list.title}</h3>
                                              </CardContent>
                                          </Grid>
                                      </Grid>
                                  </div>
                              </Card>
                          </Grid>
                  </Grid>
                       }
                       </>
                        )
                })
                }  
                 <Footer/>
         </Grid>
         :null
            }


         </>
        );
}

export default QuizCategory;
