import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import  './Card.css'
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';

import Button from '@material-ui/core/Button';

import {Link} from  "react-router-dom"
import { Grid } from '@material-ui/core';

const useStyles = makeStyles((theme)=>({

      paper: {
        padding: theme.spacing(1),

      },
  card: {
    maxWidth: 350,
    margin:"50px",
    marginTop:"120px"

  },
  media: {
    height:50
  },
  edit:{
    float:"left",
    background:"#133b5c",
    color:"#fff",
  },
  dlt:{
    float:"right",
    background:"#E70808",
    color:"#fff",
  },

  col:{
    display:"flex",
  }

}));

export default function Cards({category}) {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  return (
    <>
      <Card className={classes.root}     variant="outlined" key={category.categoryId} >
         <Grid container className="carddata">
              <Grid item xs={4} sm={4} md={4} lg={4} className="imgpart" >
                <img className="categoryImg" src={category.image} /> 
              </Grid>
              <Grid item xs={8} sm={8} md={8} lg={8} className="optionbg" >
                <CardContent className="cardcontent">
                  <h3 className="optioncontent" id="cardText">
                      {category.title}
                  </h3>
                   </CardContent>
              </Grid>
        </Grid>
            <CardActions>
            <div>
                    <Button
                    className={classes.dlt}
                    size="small"
                    variant="contained"
                    >
                    Reset
                   </Button>
                  <Link to={`/questions/${category.categoryId}`} >
                  <Button
                    className={classes.edit}
                    size="small"
                    variant="contained"

                    >
                   Questions
                   </Button>
                  </Link>
          </div>
        </CardActions>
    </Card>

    </>
  );
}
