import React, { useState, useEffect } from 'react'
import '../style/score.css'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ReactSpeedometer from "react-d3-speedometer"
import { Link } from 'react-router-dom';
import Footer from '../footer/Footer';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function Score(props) {


    var datalist=JSON.parse(localStorage.getItem('alldata'));
    var list=datalist['list'];
    var score=datalist.score.score;
    var createdBy=datalist['createdBy'];

  debugger
  const feedbackBy=JSON.parse(localStorage.getItem('feedbackby'))
  console.log(feedbackBy)

     var value=(score/list)*100;
     console.log(value)
  if(value >= 81 && value <=100)
  {
    var text="Best Friend"  
  }
  else if(value >= 61 && value <=80)
  {
    var text="Good Friend"
  }
  else if(value >= 41 && value <=60)
  {
    var text ="Average Friend"
  }
  else if(value >= 21 && value <=40)
  {
    var text="Bad Friend"
  }
  else{
    var text ="Very Bad Friend"
  }

  const classes = useStyles();
    return (
    <>
    <div className={classes.gridContainer}>
    <Grid container>
       <Grid item xs></Grid>
      <Grid item xs={11} sm={6} md={6} lg={4} >
          <div justify="center" className={classes.head}>
              <h3 className="text1">2021 Friendship</h3>
              <h3 className="text2">Dare</h3>
          </div>
          <Card className="score-card">
               <h3 className="how">Your Score:   {score}/{list}</h3>
            <Grid container justify="center">  
                <ReactSpeedometer
                    maxValue={list}
                    value={score}
                    minValue={0}
                    className="speedometer"
                    segments={5}
                    ringWidth={60}
                    needleTransition={"easeBackInOut"}
                    segmentColors={["#7555D9"," #7555D9"," #7555D9"," #7555D9"]}
                    needleColor={"#707070"}
                    customSegmentLabels={[
                      {
                        text: "😡",
                        position: "INSIDE",
                        color:"#fff",
                        fontSize:"35px",



                      },
                      {
                        text: "😞",
                        position: "INSIDE",
                        color: "#fff",
                        fontSize:"35px",

                      },
                      {
                        text: "😐",
                        position: "INSIDE",
                        color: "#fff",
                        fontSize:"35px",

                      },

                      {
                        text: "😊",
                        position: "INSIDE",
                        color: "#fff",
                        fontSize:"35px",

                      },
                      {
                        text: "😍",
                        position: "INSIDE",
                        color:"#fff",
                        fontSize:"35px",


                      },
                    ]}
                /> 
             </Grid>
             <Grid container justify="center">
             <h4 className="score-value">{text}</h4>
             </Grid>
            <Link to="/"><button className="copy">Create Your Quiz</button></Link>  
              <div className="sharelink">
                <h3 className="knows">Who knows {createdBy} best:</h3>
              </div>
                              <Grid container justify="center">                   
                                    <table className="table-data">
                                        <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Score</th>
                                        </tr>
                                        </thead>
                                       <tbody>
                                       <tr>
                  <td>{feedbackBy}</td>
                  <td>{score}</td>
                                        </tr>
                                       </tbody>
                                    </table>                   
                              </Grid>              
        </Card>

    </Grid>
    <Grid item xs>

    </Grid>

    </Grid>
    <br/>
    <Grid container>
    <Grid item xs>

    </Grid>
    <Grid item xs={11} sm={6} md={6} lg={4}>

    </Grid>
    <Grid item xs>

    </Grid>
    </Grid>
    <Footer/>
    </div>

    </>
);
}

export default Score
