import React, { useState, useEffect } from 'react'
import '../style/score.css'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ReactSpeedometer from "react-d3-speedometer"
import { Link } from 'react-router-dom';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function Score(props) {
  console.log(props)
  const score=props.location.state.data.score;
  const createdBy=props.location.state.createdBy.createdBy;
  const feedbackBy=props.location.state.feedbackBy;
  const list=props.location.state.quizlist.quizlist.length;

  const [speedo,setSpeedo]=useState(0)
  const classes = useStyles();
    return (
    <>
    <div className={classes.gridContainer}>
    <Grid container>
       <Grid item xs></Grid>
      <Grid item xs={11} sm={6} md={6} lg={4} >
          <div justify="center" className={classes.head}>
              <h3 className="text1">2021 Friendship</h3>
              <h3 className="text2">Dare</h3>
          </div>
          <Card className="score-card">
               <h3 className="how">Your Score:   {score}/{list}</h3>
            <Grid container justify="center">  
                <ReactSpeedometer
                    maxValue={list}
                    value={score}
                    minValue={0}
                    className="speedometer"
                    segments={5}
                    segmentColors={["#FF3300","#FFB605","#00B71D","#048E15"]}
                    needleColor={"#707070"}
                    currentValueText="Happiness Level"
                    customSegmentLabels={[
                      {
                        text: "ðŸ‘Ž",
                        position: "INSIDE",
                        color:"#fff",
                        fontSize:"15px"


                      },
                      {
                        text: "Bad",
                        position: "INSIDE",
                        color: "#fff",

                      },
                      {
                        text: "Ok",
                        position: "INSIDE",
                        color: "#fff",

                      },

                      {
                        text: "Good",
                        position: "INSIDE",
                        color: "#fff",

                      },
                      {
                        text: "best",
                        position: "INSIDE",
                        color:"#fff",


                      },
                    ]}
                /> 
            </Grid>
            <Link to="/choosecategory"><button className="copy">Create Your Quiz</button></Link>  
              <div className="sharelink">
                <h3 className="knows">Who knows {createdBy} best:</h3>
              </div>
                              <Grid container justify="center">                   
                                    <table className="table-data">
                                        <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Score</th>
                                        </tr>
                                        </thead>
                                       <tbody>
                                       <tr>
                  <td>{feedbackBy}</td>
                  <td>{score}</td>
                                        </tr>
                                       </tbody>
                                    </table>                   
                              </Grid>              
        </Card>

    </Grid>
    <Grid item xs>

    </Grid>

    </Grid>
    <br/>
    <Grid container>
    <Grid item xs>

    </Grid>
    <Grid item xs={11} sm={6} md={6} lg={4}>

    </Grid>
    <Grid item xs>

    </Grid>
    </Grid>

    </div>
    </>
);
}

export default Score
