import { Card, CardContent, Grid, makeStyles, TextField } from '@material-ui/core';
import React,{useState} from 'react';
import "./createQuiz.css"
import AdminLink from '../management/AdminLinks';
import services from '../../services/services';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    gridContainer: {
      paddingTop:"20px",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
    headPart:{
        backgroundColor:"#133b5c",
      color:"#ffff"
    },
    icon:{
        color:"#fff"
    },
    categorytitle:{
        color:"#133b5c",
        fontSize:"35px",
        fontWeight:"600",
        textAlign:"center"
    },
    heading: {
      fontSize: theme.typography.pxToRem(20),
      fontWeight: theme.typography.fontWeightRegular,
    },
  }));

function CreateQuiz() {
    const classes = useStyles();
    const data = JSON.parse(localStorage.getItem('friend'));
    const jwt = data['jwt'];
    var userId = data['userId'];
    const [question, setQuestion] = useState(
        [
          {
            questionTitle: '',
            optionsArray: [
              { optionLabel: "" },
              { optionLabel: "" }
            ]
          },
        ]
      )

      const [category, setCategory] = useState([])
      const [categoryOption, setCategoryOption] = useState([])
      const addQuestion = () => {
        setQuestion([
          ...question,
          {
            questionTitle: '',
            optionsArray: [
              { optionLabel: "", optionImg: "" },
              { optionLabel: "", optionImg: "" }
            ]
          }
        ])
      }

      const removeQuestion = (index) => {
        const list = [...question]
        list.splice(index, 1);
        setQuestion(list);
      }
      const addOption = (questionIndex) => {
        const options = question[questionIndex];
        options.optionsArray.push({ optionLabel: "", optionImg: "" });
        const previous = question.slice(0, questionIndex);
        const current = [options];
        const next = question.slice(questionIndex + 1, question?.length);
        setQuestion([
          ...previous,
          ...current,
          ...next
        ]);
      }

      const removeOption = (questionIndex, answerIndex) => {
        const options = question[questionIndex];
        const newArray = options.optionsArray.filter((value, index) => index !== answerIndex);
        const previous = question.slice(0, questionIndex);
        const current = [{ ...options, optionsArray: [...newArray] }];
        const next = question.slice(questionIndex + 1, question?.length);
        setQuestion([
          ...previous,
          ...current,
          ...next
        ]);
      }
      const handleImage = async(event) => {
         var imgpath=event.target.files[0]
        const data = imgpath.name;
        console.log(data)
        const currentTime = new Date();
        const time = currentTime.getTime();
        const ext= data.split('.')
        console.log(ext[0],ext[1])
        var file_path=ext[0]+time+'.'+ext[1];
        var myimg=data.replace(data,file_path)
        var img=new FormData()
        img.append("bannerImg",imgpath)
        img.append("newName",myimg)
        const options = {
          method: 'POST',
          headers: {
            "Apiuserid": "49507884",
            "Scope": "In.QuizApp.INSERT",
            'Content-Type': 'multipart/form-data',
            "Authorization": `quizApp-oauthtoken ${jwt}`,
          }
        }
        const result = await services.bannerImage(img, options);

       console.log(result)
      }
      const handleOption = (questionIndex, index, event) => {
        const values = [...question];

        values[questionIndex].optionsArray[index].optionLabel = event.target.value;
        setQuestion(values);
      }
      const handleQuestion = (index, event) => {

        const values = [...question];

        values[index].questionTitle = event.target.value;
        setQuestion(values);

      }
      const handleSubmit = async() => {
            const quizTitle=document.getElementById(title).value
            const quizSlug=document.getElementById(slug).value
            const language=document.getElementById(language).value

             const fileUpload={
              quizTitle:quizTitle,
              quizSlug:quizSlug,
              language:language,
              priority:1,
              bannerImage:file_path,
              questionArray:question

            }
            console.log(fileUpload)
            const options = {
              method: 'POST',
              headers: {
                "Apiuserid": "49507884",
                "Scope": "In.QuizApp.INSERT",
                'Content-Type': 'multipart/form-data',
                "Authorization": `quizApp-oauthtoken ${jwt}`,
              }
            }
            const result = await services.createQuiz(fileUpload, options)
            console.log(result)

      }

    return (
        <div className={classes.root}>
        <AdminLink/>
        <Grid container className="detailView">
        <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div className="createnumber">
                        <h3>Title</h3>
                    </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <input  type="text" id="title" className="title-text"
                            name="title"
                            placeholder="Title"
                            />
                               </Grid>
                      </Card>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                  <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div className="createnumber">
                        <h3>Slug</h3>
                    </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <input  type="text" id="slug" className="title-text"
                            name="slug"
                            placeholder="Slug"
                            />
                               </Grid>
                      </Card>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                   <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div className="createnumber">
                        <h3>language</h3>
                    </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <input  type="text" id="language" className="title-text"
                            name="language"
                            placeholder="language"
                            />
                               </Grid>
                      </Card>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                  <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div className="createnumber">
                        <h3>upload</h3>
                    </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <span  id="upload" className="title-text"
                            name="upload"
                            >Please Upload File</span>
                            <input type="file" accept="image/*" id="input" name="input"   onChange={event => handleImage(event)} />
                                <div className="create-label">
                                <label className="create-img" htmlFor="input">
                                    <span>  Choose your Photo</span>
                                </label>
                                </div>
                               </Grid>
                      </Card>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                  {
                question.map((que, index) => (
                  <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} className="card">
                    <div className="createnumber">
                        <h3>Question</h3>
                    </div>
                    <div className="createnumber1">
                    {question.length > 1 &&<button className="delete" onClick={() => removeQuestion(index)}>X</button>}
                   </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <input  type="text" id="question" className="que-text"
                            name="question"
                            placeholder="Enter Question"
                            onChange={event => handleQuestion(index, event)}
                            />
                               </Grid>
                               {que.optionsArray.map((option, optIndex) => (
                               <>
                              <div className="answerContainer">
                               <input type="radio"/>
                               <textarea className="create-answer" placeholder="Answer"  onChange={event => handleOption(index, optIndex, event)}/>
                               {optIndex > 1 &&<button className="delete"  onClick={() => removeOption(index, optIndex)}>X</button>}
                              </div>
                              </>
                               ))}
                              <div className="create-label">
                              {
                              <button className="addoption"   onClick={() => addOption(index)}>Add Option</button>}
                              </div>
                      </Card>
                      <div className="create-label">
                      {index === question.length - 1 &&  <button className="addoption"   onClick={() => addQuestion()}>Add Question</button>}
                     </div>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>

                ))}
                  <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4} className="card" >
                    <div>
                    <div className="createnumber">
                        <h3>Result</h3>
                    </div>
                   <div className="createnumber1">
                   <button className="delete">X</button>
                   </div>
                    </div>
                      <Card className="create-list">
                                  <Grid
                                  container
                                  justify="center"
                                 >
                               <input  type="text" id="max" className="result-text"
                            name="max"
                            placeholder="Max Score"
                            />
                            <input  type="text" id="min" className="result-text"
                            name="min"
                            placeholder="Min Score"
                            />
                            <input  type="text" id="resultTitle" className="result-text"
                            name="resultTitle"
                            placeholder="Title"
                            />
                            <input  type="text" id="phrase" className="result-text"
                            name="phrase"
                            placeholder="Phrase"
                            />
                             <span  id="upload" className="result-text"
                            name="upload"
                            >Please Upload File</span>
                            <input type="file" accept="image/*" id="input" name="input" />
                                <div className="create-label">
                                <label className="create-img" htmlFor="input">
                                    <span>  Choose your Photo</span>
                                </label>
                                </div>
                               </Grid>
                      </Card>
                      <div className="create-label">
                        <button className="addoption">Add Result</button>
                     </div>
                    </Grid>
                    <Grid item xs></Grid>
                  </Grid>
                    <div className="create-label">
                        <button className="addoption" onClick={handleSubmit}>Save</button>
                     </div>
        </Grid>

        </div>
    )
}

export default CreateQuiz
