import React, { useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputBase from '@material-ui/core/InputBase';
import { Grid } from '@material-ui/core';
import './Question.css'

const useStyles = makeStyles((theme) => ({
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),

      },
      textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: '25ch',
      },

    },

    formControl: {
      margin: theme.spacing(1),

    },

    option:{
      display:"block"
    },
    option1:{
      float:"left"
    },
    img:{
      float:"right",
      width:"50px"
    },
    dropdown:{
        zIndex: 1,
        transform: "translate(14px, 13px) scale(1)",
        padding: "10.5px 14px"
    }
  }))

function QuestionForm() {
    const classes = useStyles()
    // const [option,setOption]= useState([{option:'',file:''}])

    // const handleAddFields = () => {
    //   setOption([...option, { firstName: '', lastName: '' }])
    // }

    // const handleRemoveFields = (index) => {
    //   const values  = [...option];
    //   values.splice(index, 1);
    //   setOption(values);
    // }
    return (
       <>
          <div className="grid-container">
                <Grid
                  container
                  spacing={4}
                  className={classes.gridContainer}
                  justify="center"
                >
                    <Grid item xs={12} sm={6} md={8}>
                    <FormControl variant="outlined" className={classes.formControl} size="small" fullWidth>
                    <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
                    <Select
                      labelId="demo-simple-select-outlined-label"
                      id="demo-simple-select-outlined"
                      label="Age"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                    </Grid>
                    </Grid>    
                    <form className={classes.root} noValidate autoComplete="off">
                    <Grid
                      container
                      spacing={4}
                      className={classes.gridContainer}
                      justify="center"
                    >
                   <Grid item xs={12} sm={8} md={8}>
                   <Grid
                      container
                      spacing={4}
                      className={classes.gridContainer}
                      justify="center"
                    >
                       <Grid item xs={10} sm={8} md={10}>
                       <TextField id="outlined-basic" label="Outlined" variant="outlined" size="small"className="text"fullWidth />
                       </Grid>
                       <Grid item xs={2} sm={2} md={2}>
                       <IconButton>
                         <AddIcon/>
                       </IconButton>
                       </Grid>
                   </Grid>
                  </Grid>
                 </Grid>
                  </form>
          </div>
       </>

    )
}

export default QuestionForm
