import React,{useState,useRef, useEffect} from 'react'
import '../style/sharelink.css'
import {  Grid, Card } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import services from '../../services/services';
import { Redirect, useParams } from 'react-router-dom';
import PopUp from '../../control/PopUp';
import InstaModel from './InstaModel';
import Footer from '../footer/Footer';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },

  }));

function ShareLink(props) {


    const classes = useStyles();
    const [allResult,setAllResult]=useState([])
    const [openPopup,setOpenPopup] = useState(false);
    const data = JSON.parse(localStorage.getItem('createId'));
    const createId= data['quizId'];


    const textAreaRef = useRef(null);

    function copyToClipboard(e) {
    textAreaRef.current.select();
    document.execCommand('copy');
    e.target.focus();

    };
   // };

   useEffect(() => {
       getCreaterResult();
   }, [])

   const getCreaterResult = async()=>{
   const dataid = {
    quizId: createId,
  }
  const options = {
    method: 'POST',
    headers: {
      "Apiuserid": "49507884",
      "Scope": "In.QuizApp.Read",
    }
  }
  const result = await services.getCreaterResult(dataid, options)
  setAllResult(result.data.data)
   }
   const refresh =()=>{
    window.location.reload(false);
   }

        return (
        <>
        <div className={classes.gridContainer}>
            <Grid container>
                <Grid item xs></Grid>
                <Grid item xs={11} sm={6} md={6} lg={4} >
                    <div justify="center" className={classes.head}>
                        <h3 className="text1">2021 Friendship</h3>
                        <h3 className="text2">Dare</h3>
                    </div>

                    <Card className="form-card">
                        <div className="sharelink">
                            <h3 className="challenge">Your Challenge is ready</h3>
                            <img src="/assets/image/friend.svg"/>
                            <h3 className="this-link">Share this link with your friends</h3>
                            <input  ref={textAreaRef} className="quizInput" defaultValue={`http://localhost:3000/friendlink/${createId}`} />

                            <br />
                        </div>
                        <div className="btn-wrapper">
                            <button  onClick={copyToClipboard} className="copy"> Copy Link</button>
                        </div>
                        <div className="btn-list">
                            <button className="whatsapp">
                                <a href={`whatsapp://send?text=https://webestfriends.com/friendlink/${createId}`}>
                                  <img src="/assets/image/whatsapp.png" className="whatsapp-img"/><span className="status">WhatsApp Status</span> 
                                </a>
                            </button>                            
                            <button className="instagram" onClick={ ()=> setOpenPopup(true)}>
                                <a href="#">
                                  <img src="/assets/image/instagram.png" className="instagram-img" /><span className="instagram-status">Add to Instagram Bio</span>
                                </a>
                            </button> 
                           {/* <button className="sharing">
                                <a href="#">
                                  <img src="/assets/image/sharing.svg"className="sharing-img" /><span className="status">Share</span>
                                </a>
                          </button>        */}
                       </div>
                       <div className="result-wrapper">
                          <button className="result" onClick={refresh}> View Results</button>
                       </div>                   


                             {
                               allResult?.length?
                               <>
                               <div className="sharelink">
                               <h3 className="challenge">Who knows you the best:</h3>
                           </div>
                               <Grid container justify="center">                   
                                <table className="table-data">
                                    <thead>
                                        <tr>
                                            <th>Name</th>
                                            <th>Score</th>
                                        </tr>
                                    </thead>
                                    {
                          allResult?.map((tabledata)=>{
                              return(
                                    <tbody>
                                    <tr>
                                        <td>{tabledata.feedbackBy}</td>
                                        <td>{tabledata.score}</td>
                                    </tr>
                                    </tbody>
                                    )
                                  })
                                }
                                </table>                   
                          </Grid>
                          </>:null
                             }  



                     <Grid item xs> </Grid>
                    </Card>
                </Grid>
                <Grid item xs>

                </Grid>
            </Grid>
            <PopUp
                    openPopup={openPopup}
                    setOpenPopup={setOpenPopup}
                    maxWidth="sm"
                    text="How to add this link to your Instagram BIO"
                    >
                     <InstaModel/>  
                </PopUp>
            <br/>
            <Footer/>
        </div>

        </>
        );
}

export default ShareLink
