import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {Link} from  "react-router-dom"

const useStyles = makeStyles((theme)=>({

      paper: {
        padding: theme.spacing(1),

      },
  card: {
    maxWidth: 350,
    margin:"50px",
    marginTop:"120px"

  },
  media: {
    height:50
  },
  edit:{
    float:"left",
    margin:"10px",
    background:"#133b5c",
    color:"#fff",
    '&: hover':{
    color: "#133b5c !important",
    }
  },
  dlt:{
    float:"right",
    margin:"10px",
    background:"#E70808",
    color:"#fff",
    '&: hover':{
    color: "#133b5c !important",
    }
  },

  col:{
    display:"flex",
  }

}));

export default function Cards({category}) {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  return (
    <>
      <Card className={classes.root}     variant="outlined" key={category.categoryId} >
            <CardContent>
               <Typography gutterBottom         variant="h5"component="h2" >
                   {category.title}
               </Typography>
                <CardMedia>
                  <img className={classes.media} src={category.image}/>
                </CardMedia>
            </CardContent>
            <CardActions>
            <div>
                    <Button
                    className={classes.edit}
                    size="medium"
                    variant="contained"

                    >
                    Edit
                    </Button>
                    <Button
                    className={classes.dlt}
                    size="medium"
                    variant="contained"

                    >
                    Reset
                   </Button>
                  <Link to={`/questions/${category.categoryId}`} >
                  <Button
                    className={classes.edit}
                    size="medium"
                    variant="contained"

                    >
                   Questions
                   </Button>
                  </Link>
          </div>
        </CardActions>
    </Card>

    </>
  );
}
