import React, { useEffect, useState } from 'react';
import services from '../../services/services';
import PlayQuizPart from './PlayQuizPart';


function PlayQuizPartApi(props) {
   const slug=props.location.state.slug.slug;
   const [quiz,setQuiz]=useState([])
   const [que,setQue]=useState([])
   const data = JSON.parse(localStorage.getItem('friend'));
   const jwt = data['jwt'];
   useEffect(() => {
       getQuizBySlug();
   }, [])

   const getQuizBySlug = async () => {
        const data={
          quizSlug:slug
        }
       const options = {
           method: 'Post',
           headers: {
               "Apiuserid": "49507884",
               "Scope": "In.QuizApp.READ",
           }
       }
       const result = await services.getQuizBySlug(data,options)
       console.log(result.data.data)
       setQuiz(result.data.data)
       setQue(JSON.parse(result.data.data[0].questionData))
   };



    return (
        <div>
            {
               <PlayQuizPart/>
            }

        </div>
    )
}

export default PlayQuizPartApi
