import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import '../style/questionlist.css'
import '../style/playquiz.css'
import LinearProgress from '@material-ui/core/LinearProgress';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
      width:"100%",
    },
    colorPrimary: {
      backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
    },
    bar: {
      borderRadius: 5,
      backgroundColor: '#FFDD52',
    },
  }))(LinearProgress);

const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      pro:{
          textAlign:"center"
      }

  }));
function PlayQuiz({quizlist,quizId,createdBy}) {
  console.log(createdBy)
  debugger
 const[score,setScore]=useState(0)
 const [activeStep, setActiveStep] = React.useState(0);
 const [meter,setMeter]=useState(false);
 const json =localStorage.getItem("feedbackby")

  let quiz = [
    {
        label: quizlist?.question,
        options: quizlist?.options,
    },
];

    const classes = useStyles();
    if (quizlist?.length) {
      quiz = quizlist;
    }


    const maxSteps = quiz.length;
    const handleSkip = () => { setActiveStep(prevActiveStep => prevActiveStep + 1); };
    const handleSelect = async(optId,label)=>{
       if(label===quiz[activeStep].answer){
            increaseScore();

       }


    }

const handleNext = (optId,label)=>{

  if( maxSteps!==quiz[activeStep].qusId){
    setActiveStep(prevActiveStep => prevActiveStep + 1)
  }
  else{
    handleSubmit();
  }
  handleSelect(optId,label);

}
const increaseScore = ()=>{
  setScore(score+1)
}
const handleSubmit = async() =>{
    const data={
      quizId:quizId,
      feedbackBy:json,
      score:score
    }
    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
      }
    }
    const result = await services.getResult(data, options)
     setMeter(result.data.data)
}
if(meter===true)
{

  return(<Redirect to={{pathname:"/score" ,state:{data:{score},quizlist:{quizlist},createdBy:{createdBy},feedbackBy:{json}}}} push={true} />)

}
    return (
        <div className={classes.gridContainer}>
        <Grid container>
           <Grid item xs></Grid>
            <Grid item xs={11} sm={6} md={6} lg={4} >
                 <div justify="center">
                    <h3 className="text1">2021 Friendship</h3>
                    <h3 className="text2">Dare</h3>
                 </div>
                 <div justify="center">     
                    <h4 className="question">Question <span>1/15</span></h4>
                 </div>
                 <div justify="center">
                    <BorderLinearProgress variant="determinate" value={50} />
                 </div>
            <Card className="questioncard">
                <h3 className="questionname">{quiz[activeStep].qusId}:{quiz[activeStep].title}</h3>


                    <div className="carddesign">
                    {
                    quiz[activeStep]?.options?.map((options,index)=>{

                    return(
                        <Grid
                      container
                      className={'optionpart'}
                      correct={quiz[activeStep].answer}
                      key={index}
                      onClick={()=>{handleNext(options.optionId,options.optionLabel)}}
                      >
                      <Grid item xs={4} sm={4} md={4}lg={4}>
                      {options.optionImg.length
                                ?<img className="optionImg" 
                                  src={options.optionImg}/>
                                :<span></span>
                              }
                      </Grid>
                      <Grid item xs={8} sm={8} md={8}lg={8} className="optionbg" >
                        <CardContent className="cardcontent">
                          <h3 className="optioncontent" id="cardText">
                        {options.optionLabel}
                          </h3>
                        </CardContent>
                      </Grid>
                      </Grid>
                    )

                    })
                    } 
                </div>

                <div className="start-wrapper">

                 {
                 maxSteps!==quiz[activeStep].qusId?
                 <button className="skip"
                 onClick={()=>{ maxSteps!==quiz[activeStep].qusId?handleSkip():handleSubmit() }}
                 >Skip the Question</button>:null
                  } 
                </div> 
         </Card>
            </Grid>
            <Grid item xs></Grid>
       </Grid>
  </div>
    )
}

export default PlayQuiz
