import React, { useState, useEffect } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import RemoveCircleIcon from '@material-ui/icons/RemoveCircle';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import Icon from '@material-ui/core/Icon';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputBase from '@material-ui/core/InputBase';
import { Grid } from '@material-ui/core';
import './Question.css'
import services from '../../../services';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),

    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
      width: '25ch',
    },

  },
  formControl: {
    margin: theme.spacing(1),

  },
  option: {
    display: "block"
  },
  option1: {
    float: "left"
  },
  img: {
    float: "right",
    width: "50px"
  },

  button: {
    margin: theme.spacing(1),
  },
  dropdown: {
    zIndex: 1,
    transform: "translate(14px, 13px) scale(1)",
    padding: "10.5px 14px"
  }
}))

function QuestionForm() {
  const classes = useStyles()
  const [question, setQuestion] = useState(
    [
      {
        questionTitle: '',
        optionsArray: [
          { optionLabel: "", optionImg: "" },
          { optionLabel: "", optionImg: "" }
        ]
      },
    ]
  )

  const [category, setCategory] = useState([])
  const [categoryOption, setCategoryOption] = useState([])
  const data = JSON.parse(localStorage.getItem('friend'));
  const jwt = data['jwt'];
  var userId = data['userId'];

  useEffect(() => {
    getCategory();
  }, [])

  const getCategory = async () => {
    const options = {
      method: 'Get',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.READ",
        "Authorization": `quizApp-oauthtoken ${jwt}`,
      }
    }
    const result = await services.getCategory(options);
    setCategory(result.data.data)
  };

  const addQuestion = () => {
    setQuestion([
      ...question,
      {
        questionTitle: '',
        optionsArray: [
          { optionLabel: "", optionImg: "" },
          { optionLabel: "", optionImg: "" }
        ]
      }
    ])
  }

  const removeQuestion = (index) => {
    const list = [...question]
    list.splice(index, 1);
    setQuestion(list);
  }
  const addOption = (questionIndex) => {
    const options = question[questionIndex];
    options.optionsArray.push({ optionLabel: "", optionImg: "" });
    const previous = question.slice(0, questionIndex);
    const current = [options];
    const next = question.slice(questionIndex + 1, question?.length);
    setQuestion([
      ...previous,
      ...current,
      ...next
    ]);
  }

  const removeOption = (questionIndex, answerIndex) => {
    const options = question[questionIndex];
    const newArray = options.optionsArray.filter((value, index) => index !== answerIndex);
    const previous = question.slice(0, questionIndex);
    const current = [{ ...options, optionsArray: [...newArray] }];
    const next = question.slice(questionIndex + 1, question?.length);
    setQuestion([
      ...previous,
      ...current,
      ...next
    ]);
  }

  const handleChange = (event) => {
    setCategoryOption(event.target.value);
  };

  const handleOption = (questionIndex, index, event) => {
    const values = [...question];

    values[questionIndex].optionsArray[index].optionLabel = event.target.value;
    setQuestion(values);
  }

  const handleImage = async(questionIndex, index, event) => {
    const values = [...question];
     var imgpath=event.target.files[0]
    const data = imgpath.name;
    const currentTime = new Date();
    const time = currentTime.getTime();
    const ext= data.split('.')
    var file_path= questionIndex+'_' + index+'_' +ext[0]+time+'.'+ext[1];
    var myimg=data.replace(data,file_path)
    var img=new FormData()
    img.append("optionImg",imgpath);
    img.append("newName",myimg)
    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
        'Content-Type': 'multipart/form-data',
        "Authorization": `quizApp-oauthtoken ${jwt}`,
      }
    }
    const result = await services.getImage(img, options);
    values[questionIndex].optionsArray[index].optionImg=file_path;
    setQuestion(values);
  }
  const handleQuestion = (index, event) => {

    const values = [...question];

    values[index].questionTitle = event.target.value;
    setQuestion(values);

  }

  const addTask = async () => {

    // const fileUpload = new FormData();



    //data.append('questionsArray', JSON.stringify(question));
        // let questionArray = [];
    // for (const dataKey of question) {
    //   let optionArray = dataKey?.optionsArray;
    //   data.append('hello',question);
    //   for (const previewKey of dataKey.optionsArray) {
    //     optionArray.push(previewKey);
    //   }
    //   questionArray.push({
    //     question: dataKey.question,
    //     options: optionArray
    //   });
    // }

    // fileUpload.append("category", categoryOption);
    // fileUpload.append("userId", userId);
    // fileUpload.append("questionArray", JSON.stringify(question));


    const fileUpload={
      category:categoryOption,
      userId:userId,
      questionsArray:question
    }

    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
        'Content-Type': 'multipart/form-data',
        "Authorization": `quizApp-oauthtoken ${jwt}`,
      }
    }
    const result = await services.addQuestion(fileUpload, options)
    console.log(result)
  }

  return (
    <div>
      <Container>
        <h1>Create Question</h1>
        <form className={classes.root} noValidate autoComplete="off">
          <Grid
            container
            spacing={3}
            className={classes.gridContainer}
            justify="center"
          >
            <Grid item xs={12} sm={6} md={5}>
              <div>
                <FormControl className={classes.formControl} fullWidth>
                  <InputLabel id="demo-simple-select-label">Category</InputLabel>
                  <Select
                    label="Category"
                    id="category"
                    value={categoryOption}
                    onChange={handleChange}
                  >

                    {
                      category.map((option, index) => {
                        return (

                          <MenuItem key={index} value={option.categoryId}>{option.title}</MenuItem>
                        )
                      })
                    }
                  </Select>
                </FormControl>

              </div>
              {
                question.map((que, index) => (
                  <div>
                    <Grid key={index}
                      container
                      spacing={1}
                      className={classes.gridContainer} >
                      <Grid item xs={7} sm={7} md={7}>
                        <TextField label="Question" id="standard-size-small"
                          fullWidth
                          defaultValue={que.question}
                          onChange={event => handleQuestion(index, event)}
                          type="question" size="small" />
                      </Grid>
                      <Grid item xs={4} sm={4} md={4}>
                        {question.length > 1 && <IconButton
                          onClick={() => removeQuestion(index)}
                        >
                          <DeleteIcon />
                        </IconButton>}
                        {index === question.length - 1 && <IconButton

                          onClick={() => addQuestion()}
                        >
                          <AddCircleIcon />
                        </IconButton>}
                      </Grid>

                    </Grid>
                    {que.optionsArray.map((option, optIndex) => (
                      <div key={optIndex} className={classes.option}>
                        <Grid
                          container
                          spacing={3}
                          className={classes.gridContainer}                   >
                          <Grid item xs={12} sm={6} md={8} lg={7}>
                            <TextField
                              id="option2"
                              label="Option"
                              type="option2"
                              size="small"
                              defaultValue={option.optionLabel}
                              className={classes.option1}
                              fullWidth
                              onChange={event => handleOption(index, optIndex, event)}
                            />
                          </Grid>
                          <Grid item xs={12} sm={6} md={4}>
                            <div>
                              <div className="wrapper">
                                <div className="file-upload2">
                                  <input type="file" className="option2"
                                    defaultValue={option.optionImg}
                                    onChange={event => handleImage(index, optIndex, event)}
                                  />
                                  <img src="/assets/image/option.png" height="40px" />
                                </div>
                              </div>

                              {

                                optIndex > 1 && <IconButton
                                  onClick={() => removeOption(index, optIndex)}
                                >

                                  <DeleteIcon
                                    className="remove" />
                                </IconButton>

                              }
                              {
                                que?.optionsArray.length - 1 === optIndex && <IconButton
                                  onClick={() => addOption(index)}
                                >
                                  <AddIcon
                                    className="remove" />
                                </IconButton>
                              }

                            </div>

                          </Grid>
                        </Grid>
                      </div>
                    ))}
                  </div>


                ))}
              <div>
                <Button
                  className="btn"
                  size="medium"
                  variant="contained"
                  onClick={addTask}
                >
                  Create
                 </Button>

              </div>
            </Grid>
          </Grid>
        </form>
      </Container>
    </div>
  )
}

export default QuestionForm
