import React,{useState,useEffect} from 'react'
import '../style/friendlink.css'
import {  Grid,Card} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useParams,Redirect, useHistory } from 'react-router-dom';
import services from '../../services/services';
import Footer from '../footer/Footer';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      head:{
          textAlign:"center"
      },
      list:{
        margin:"25px auto",
        textAlign:"center"
      },
      add:{
          width:"100%"
      }


  }));

function FriendLink() {
const classes = useStyles();
const history = useHistory()
const [quiz,setQuiz]=useState([])
const [list,setList]=useState([]) 
const [right,setRight]=useState(false) 
const[loader,setLoader]=useState(false)
var {id} = useParams();
console.log(id,typeof id)
    useEffect(() => {
        if (localStorage.getItem('createId') != null) {
            JSON.parse(localStorage.getItem('createId')).map(item => {

                console.log(item,typeof item)
                if (item === id) {
                    history.push({pathname:"/sharelink",state:{quizId:id}});
                }

            });
        }
        getQuiz();
    }, [])

   const getQuiz = async () => {
       const data={
            quizId:id
       }
       const options = {
           method: 'GET',
           headers: {
               "Apiuserid": "49507884",
               "Scope": "In.QuizApp.READ",

           }
       }
       const result = await services.getQuiz(data,options)
       setQuiz(result.data.data)
       setList(result.data.data.quizQuestion)

    };



    //         if(localStorage.getItem('createId') != null)
    // {
    //         var alldata=JSON.parse(localStorage.getItem('alldata'))
    //          var feedbackId=alldata['quizId']
    //     if(feedbackId===id)
    //     {
    //         return<Redirect to="/score"/>
    //     }
    // }



 const playby=()=>{
     var name=document.getElementById("feedbackBy").value;

     if((name == "") || (name==null) || (name.length<=2))
      {
         alert("please enter required name");

      }
      else{
        localStorage.setItem("feedbackby",name);
        setRight(true)
      }

 }
 if(right)
 {
     return(<Redirect to={{
        pathname: '/quizlink',
        state:{quizQuestions:quiz}}}push={true}/>)
 }
    return (
        <>

            <div className={classes.gridContainer}>
            <Grid container>
                <Grid item xs></Grid>
                <Grid item xs={11} sm={6} md={6} lg={4}>                    
                    <div justify="center" className={classes.head}>
                        <h3 className="friendship">2021 Friendship</h3>
                        <h3 className="dare">Dare</h3>
                    </div>
                    <Card className="form-card">
                       <h3 className="how">How well do you know {quiz.createdBy}?</h3>
                       <h4 className="First">Name</h4>
                           <input type="text" id="feedbackBy" className="name" placeholder="Enter Your Name"/>
                       <br />
                       <div className="friendStart">
                           <button className="started" onClick={playby}> Start</button>
                        </div>
                            <h3 className="how">Who Knows {quiz.createdBy} Best ?</h3>
                    </Card>
                </Grid>
              <Grid item xs></Grid>

            </Grid>
            <br/>
            <Grid container>
                <Grid item xs></Grid>
                <Grid item className={classes.list} xs={11} sm={6} md={6} lg={4}>
                    <a href="#">
                       <img src="/assets/image/Image5.png" className={classes.add}/>
                    </a>
                </Grid>
                <Grid item xs></Grid>
            </Grid>
  {/* {
       list?.map((question,index)=>{
          return( <Grid container justify="center" key={index}>

                     <Grid className="paragrid"item xs={11} sm={6} md={6} lg={4}>
                      <p className="list-para">{question.title}</p> </Grid>

  </Grid>
   )
   })
  } */}
         <Footer/>
        </div>


        </>
    );
    }

export default FriendLink
