import React,{useState,useEffect} from 'react'
import '../style/category.css'
import { makeStyles, Grid, Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import services from '../../services/services'
import { NavLink, Link, Redirect } from 'react-router-dom';
import { unstable_renderSubtreeIntoContainer } from 'react-dom';


const useStyles = makeStyles({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    grid:{
      marginBottom:"20px"
    },
      cardImg:{
            float:"left",
            padding:"20px",
            width:"80px"
      },
  });

function UserCategory() {
    const classes = useStyles();

    const[category,setCategory] = useState([])

    useEffect(() => {
         getUserCategory();
    }, [])

    const  getUserCategory = async () => {
        const options = {
            method: 'Get',
        }
        const result = await services.getUserCategory(options)
        console.log(result)
        setCategory(result.data.data)
    };

    const handleClick =(id)=>{

     localStorage.setItem('categoryId', JSON.stringify(id)); 

    }
  return (
    <Grid
      container
      className={classes.gridContainer}
      justify="center"
    >
      <Grid item xs={12} sm={6} md={3}>
      <h3 className="text1">Choose your</h3>
      <h3 className="text2">Category</h3>
      </Grid>
      {
        category.map((list,index)=>{
          return(

      <Grid
      container
      justify="center"
      className={classes.grid}
    >

       <Grid item xs={12} sm={6} md={6}lg={4} >

      <Card className="card" key={index} onClick={()=>{handleClick(list.categoryId)}} >

      <div>  
      <Link to="/form">    
        <Grid
      container
    >

      <Grid item xs={4} sm={4} md={4}lg={4} >
             <img className="cardImg" src={`http://192.168.1.60/oct20/quizApp/${list.image}`}/>
             </Grid>
             <Grid item xs={8} sm={8} md={8}lg={8} className="content-bg" >
             <CardContent>
          <h3 className="content">
         {list.title}
          </h3>
        </CardContent>
             </Grid>
             </Grid>
             </Link>
      </div>

    </Card>

      </Grid>



    </Grid>

     )
    })
  }  


    </Grid>
  );
}

export default UserCategory
