import React,{useState,useEffect} from 'react'
import '../style/friendlink.css'
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useParams } from 'react-router-dom';
import services from '../../services/services';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      head:{
          textAlign:"center"
      }

  }));

function FriendLink() {
const classes = useStyles();
const [quiz,setQuiz]=useState([])

   const { id } = useParams()
   useEffect(() => {
       getQuiz();
   }, [])

   const getQuiz = async () => {
       const data={
            quizId:id
       }
       const options = {
           method: 'GET',
           headers: {
               "Apiuserid": "49507884",
               "Scope": "In.QuizApp.READ",

           }
       }
       const result = await services.getQuiz(data,options)
       setQuiz(result.data.data)
    };
    return (
        <>
            <div className={classes.gridContainer}>
                <Grid container>
                    <Grid item xs>

                    </Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4}>                    
                        <div justify="center" className={classes.head}>
                            <h3 className="friendship">2021 Friendship</h3>
                            <h3 className="dare">Dare</h3>
                        </div>
                        <Card className="form-card">
                            <h3 className="how">How well do you know abcd?</h3>
                            <form >

                                 <h4 className="language">First Name</h4>
                                <input type="text"  className="name" placeholder="FirstName..." /><br />
                                <div className="start-wrapper">
                                    <button className="started"> Start</button>
                                </div>
                            </form>
                            <h3 className="how">Who Knows Abcd Best ?</h3>
                        </Card>
                    </Grid>
                  <Grid item xs></Grid>

                </Grid>
                <br/>
                <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4}></Grid>
                    <Grid item xs></Grid>
                </Grid>
            <Grid container>
            <Grid item xs></Grid>
            <Grid className="paragrid" item xs={11} sm={6} md={6} lg={4}>
                <p className="question-para"><span>1)</span> What do you like to do in free time?</p>
                <p className="question-para"><span>1)</span> What do you like to do in free time?</p>
                <p className="question-para"><span>1)</span> What do you like to do in free time?</p>
                <p className="question-para"><span>1)</span> What do you like to do in free time?</p>
                <p className="question-para"><span>1)</span> What do you like to do in free time?</p>
            </Grid>
            <Grid item xs></Grid>
            </Grid>
            </div>
        </>
    );
    }

export default FriendLink
