import React, { useState,useEffect } from 'react'
import { Grid, Card, CardContent } from '@material-ui/core';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import '../style/questionlist.css'
import LinearProgress from '@material-ui/core/LinearProgress';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';
import Footer from '../footer/Footer';


const BorderLinearProgress = withStyles((theme) => ({
  root: {
    height: 10,
    borderRadius: 5,
    width: "100%",
  },
  colorPrimary: {
    backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
  },
  bar: {
    borderRadius: 5,
    backgroundColor: '#FFDD52',
  },
}))(LinearProgress);

const useStyles = makeStyles((theme) => ({
  gridContainer: {
    paddingLeft: "20px",
    paddingRight: "20px",
    background: "#7555D9",
    minHeight: "100vh"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  pro: {
    textAlign: "center"
  }

}));
function DummyQuestionList({ questionlist }) {

  const classes = useStyles();
  const [title,setTitle]=useState({})
  var categoryId = JSON.parse(localStorage.getItem("categoryId"));

  useEffect(() => {
    getCategory();
}, [1])

const  getCategory = async () => {
 const categoryData={
     categoryId:categoryId
 }

 const options = {
     method: 'POST',
     headers: {
         "Apiuserid": "49507884",
         "Scope": "In.QuizApp.READ",   
     }
 }
 const result = await services. getCategory(categoryData,options)
 console.log(result)
 setTitle(result.data.data)
};
  // let tutorialSteps = [
  //   {
  //     label: questionlist?.question,
  //     options: questionlist?.optionsArray,
  //   },
  // ];


  // if (questionlist?.length) {
  //   tutorialSteps = questionlist;
  // }
  const data = JSON.parse(localStorage.getItem('userInfo'));
  const createdBy = data['name'];
  const [activeStep, setActiveStep] = React.useState(0);
  const [answerList, setAnswerList] = React.useState([]);
  const [skipList, setSkipList] = React.useState([]);
  const [createQuiz, setCreateQuiz] = useState();
  const [result, setResult] = useState(false);
  const [progress, setProgress] = useState(0);
  const [count, setCount] = useState(1);
  const [clickedAns, setClickedAns] = useState(0);


  function showProgress(index) {

    var increment = Math.ceil((activeStep) / (9) * 100);

    setProgress(increment)

  }


  const handleNext = (id, label) => {
    const qusId = questionlist[0]?.qusId;
    const answer = label;
    const answerId = id;
    setClickedAns(id)
    if (5 === answerList.length) {
      setAnswerList(
        [...answerList, { qusId: qusId, answer: answer, answerId: answerId }]

      )
      console.log(answerList.length)
      debugger
      setTimeout(() => { 
        handleSubmit([...answerList, { qusId: qusId, answer: answer, answerId: answerId }]) }, 500)
    }
    else {
      setAnswerList(
        [...answerList, { qusId: qusId, answer: answer, answerId: answerId }]
      )
      console.log(answerList.length)
      setTimeout(() => {
        debugger
          var questionlist=questionlist.filter((questionlist)=>{
           return questionlist[0]
          })

          console.log(questionlist)
        setCount(count + 1)
      }, 500)


      // debugger
    }
    showProgress(questionlist[0].qusId);




  }


  const handleSkip = () => { 

    setActiveStep(prevActiveStep => prevActiveStep + 1);
   };

  const handleSubmit = async ([...answerList]) => {


    const list = {
      categoryId:categoryId,
      createdBy: createdBy,
      quizQuestions: answerList
    }

    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
      }
    }
    const result = await services.createQuiz(list, options)
    setCreateQuiz(result.data.data)
    setResult(true)
    if (result.data.code === 200) {
      setResult(true)
    }

  };

  if (result) {
    localStorage.setItem("createId", JSON.stringify(createQuiz))
    return (<Redirect to={{ pathname: "/sharelink", state: { quizId: createQuiz } }} push={true} />)
  }

  return (
    <div className={classes.gridContainer}>
      <Grid container>
        <Grid item xs></Grid>
        <Grid item xs={11} sm={6} md={6} lg={4} >
          <div justify="center">
             <h3 className="text1">{title.title}</h3>
          </div>
          <div justify="center">
  <h4 className="question">Question <span>{count}/{9}</span></h4>
          </div>
          <div justify="center">
            <BorderLinearProgress variant="determinate" value={progress} />
          </div>
          <Card className="questioncard">
            <h3 className="questionname">{count}: {questionlist[0]?.question}</h3>
            <div className="optioncard">
              {
                questionlist[0]?.optionsArray?.map((options, index) => {
                  return (
                    <Grid
                      container
                      className={
                        clickedAns === options.optionId ?
                          'clickoption' : 'nonclickoption'}
                      onClick={() => { handleNext(options.optionId, options.optionLabel) }}
                      key={index}
                      id="cardbox">
                      <Grid item xs={4} sm={4} md={4} lg={4} >
                        {options.optionImg.length ?
                          <img className="optionImg" src={options.optionImg} /> : <span></span>
                        }
                      </Grid>
                      <Grid item xs={8} sm={8} md={8} lg={8} className="optionbg" >
                        <CardContent className="cardcontent">
                          <h3 className="optioncontent" id="cardText">
                            {options.optionLabel}
                          </h3>
                        </CardContent>
                      </Grid>
                    </Grid>
                  )
                })
              }
            </div>
            <div className="start-wrapper">
              <button className="skip" onClick={() => { 9 - 1 != questionlist[0].question.length ? handleSkip() : handleSubmit() }}>Skip the Question</button>
            </div>
          </Card>
        </Grid>
        <Grid item xs></Grid>
      </Grid>
      <Footer />
    </div>
  )
}

export default DummyQuestionList
