import React, { useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputBase from '@material-ui/core/InputBase';
import { Grid } from '@material-ui/core';
import './Question.css'

const useStyles = makeStyles((theme) => ({
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),

      },

    },
    option:{
      display:"block"
    },
    option1:{
      float:"left"
    },
    img:{
      float:"right",
      width:"50px"
    },
    dropdown:{
        zIndex: 1,
        transform: "translate(14px, 13px) scale(1)",
        padding: "10.5px 14px"
    }
  }))

function QuestionForm() {
    const classes = useStyles()
    const [option,setOption]= useState([{option:'',file:''}])

    const handleAddFields = () => {
      setOption([...option, { firstName: '', lastName: '' }])
    }

    const handleRemoveFields = (index) => {
      const values  = [...option];
      values.splice(index, 1);
      setOption(values);
    }
    return (
        <div>
            <Container>
                <h1>Create Question</h1>
                <form className={classes.root} noValidate autoComplete="off">
                      <Grid
                        container
                        spacing={3}
                        className={classes.gridContainer}
                        justify="center"
                        >
                         <Grid item xs={12} sm={6} md={5}>
                       <div>
                              <FormControl className={classes.formControl} fullWidth>
                <InputLabel id="demo-simple-select-label">Category</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                 fullWidth
                >
                  <MenuItem value={10}>Ten</MenuItem>
                  <MenuItem value={20}>Twenty</MenuItem>
                  <MenuItem value={30}>Thirty</MenuItem>
                </Select>
              </FormControl>

                       </div>

                      <div>
                      <Grid
                        container
                        spacing={3}
                        className={classes.gridContainer}

                        >

                      <Grid item xs={12} sm={6} md={7}>
                      <TextField label="Question" id="standard-size-small" 
                      fullWidth
                      type="question" size="small"/>
                      </Grid>
                      <Grid item xs={12} sm={6} md={4}>
                         <IconButton
                        // onClick={() => handleRemoveFields(index)}
                      >
                        <DeleteIcon />
                      </IconButton>
                      <IconButton

                        // onClick={() => handleAddFields()}
                      >
                        <AddIcon />
                      </IconButton>
                      </Grid>
                      </Grid>
                      </div>

                          <div className={classes.option}>
                          <Grid
                        container
                        spacing={3}
                        className={classes.gridContainer}

                        >
                            <Grid item xs={12} sm={6} md={8} lg={7}>
                          <TextField
                            id="option1"
                            label="Option"
                            type="option1"
                            size="small"
                            className={classes.option1}
                            fullWidth
                            />
                            </Grid>
                            <Grid item xs={12} sm={6} md={3}>
                             <div class="wrapper">
                            <div class="file-upload">
                              <input type="file"className="option1" />
                              <img src="/assets/image/option.png" height="40px"/>
                            </div>
                          </div>
                          </Grid>
                          </Grid>
                          </div>

                          { option.map((option, index) => (
                            <div className={classes.option}>
                            <Grid
                          container
                          spacing={3}
                          className={classes.gridContainer}

                          >
                              <Grid item xs={12} sm={6} md={8} lg={7}>
                              <TextField
                            id="option2"
                            label="Option"
                            type="option2"
                            size="small"
                            className={classes.option1}
                            fullWidth
                            />
                              </Grid>
                              <Grid item xs={12} sm={6} md={4}>
                              <div>
                              <div class="wrapper">
                            <div class="file-upload2">
                              <input type="file"className="option2" />
                              <img src="/assets/image/option.png" height="40px"/>
                            </div>
                          </div>
                          <IconButton
                          onClick={() => handleRemoveFields(index)}
                        >
                          <DeleteIcon
                            className="remove" />
                        </IconButton>
                              </div>

                            </Grid>
                            </Grid>

                            </div>
                             )) }  
                              <div>
                              <Button
                                className="btn"
                                size="normal"
                                variant="contained"
                                // onClick={addTask}
                                  >
                                    Create
                                </Button>
                                <Button
                                className="btn"
                                size="normal"
                                variant="contained"
                                onClick={() => handleAddFields()}
                                >
                                   Add Option
                                  </Button>        
                              </div>
                    </Grid>

                      </Grid>                
                </form>
            </Container>
        </div>
    )
}

export default QuestionForm
