import React,{useState,useEffect} from 'react'
import '../style/friendlink.css'
import {  Grid,Card} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useParams, Link, Redirect } from 'react-router-dom';
import services from '../../services/services';
import Footer from '../footer/Footer';



const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      head:{
          textAlign:"center"
      },
      list:{
        margin:"25px auto",
        textAlign:"center"
      },
      add:{
          width:"100%"
      }


  }));

function FriendLink() {
const classes = useStyles();
const [quiz,setQuiz]=useState([])
const [list,setList]=useState([]) 
const [question,setQuestion]=useState([])

   // var categoryId = JSON.parse(localStorage.getItem("categoryId"));  
   const { id } = useParams()
    localStorage.setItem('createId',JSON.stringify(id))

console.log(id)
   useEffect(() => {
       getQuiz();
   }, [])

   const getQuiz = async () => {
       const data={
            quizId:id
       }
       console.log(data)
       const options = {
           method: 'GET',
           headers: {
               "Apiuserid": "49507884",
               "Scope": "In.QuizApp.READ",

           }
       }
       const result = await services.getQuiz(data,options)
       console.log(result)
       setQuiz(result.data.data)
       setList(result.data.data.quizQuestions)
       console.log(result)
    };

//    var dataId= JSON.parse(localStorage.getItem('createId'));
// //    var feedId=dataId['quizId']
//    debugger
//     if(id==dataId)
//     {

//         return <Redirect to="/sharelink"/>
//     }

 const playby=()=>{
     var name=document.getElementById("feedbackBy").value;
     if((name == "") || (name==null) || (name.length<=2))
      {
         alert("please enter required name");
      }
      else{
        localStorage.setItem("feedbackby",name);

      }

 }
    return (
        <>
            <div className={classes.gridContainer}>
                <Grid container>
                    <Grid item xs></Grid>
                    <Grid item xs={11} sm={6} md={6} lg={4}>                    
                        <div justify="center" className={classes.head}>
                            <h3 className="friendship">2021 Friendship</h3>
                            <h3 className="dare">Dare</h3>
                        </div>
                        <Card className="form-card">
                           <h3 className="how">How well do you know {quiz.createdBy}?</h3>
                            <form >

                                 <h4 className="First">First Name</h4>
                                <input type="text" id="feedbackBy" className="name" placeholder="FirstName..." onChange={playby} /><br />

                                <div className="friendStart">
                                <Link  to={{
                                        pathname: '/quizlink',
                                        state:{quizQuestions:quiz}}}>
                                    <button className="started"> Start</button>
                                    </Link>
                                </div>

                            </form>
                            <h3 className="how">Who Knows {quiz.createdBy} Best ?</h3>
                        </Card>
                    </Grid>
                  <Grid item xs></Grid>

                </Grid>
                <br/>
                <Grid container>
                    <Grid item xs></Grid>
                    <Grid item className={classes.list} xs={11} sm={6} md={6} lg={4}>
                        <a href="#">
                           <img src="/assets/image/Image5.png" className={classes.add}/>
                        </a>
                    </Grid>
                    <Grid item xs></Grid>
                </Grid>
      {/* {
            question?.map((question,index)=>{
              return( <Grid container justify="center">

                         <Grid className="paragrid"item xs={11} sm={6} md={6} lg={4}>
                          <p className="question-para"><span>{question.qusId})</span>{question.question}</p> </Grid>

      </Grid>
       )
       })
      } */}
             <Footer/>
            </div>

        </>
    );
    }

export default FriendLink
