import React,{useState,useEffect} from 'react'
import { useParams } from 'react-router-dom';
import {  Grid, Paper,Card, CardMedia, CardContent, Typography } from '@material-ui/core';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import '../style/playquiz.css'
import LinearProgress from '@material-ui/core/LinearProgress';
import services from '../../services/services';
import { Redirect } from 'react-router-dom';
import Footer from '../footer/Footer';

const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5,
      width:"100%",
    },
    colorPrimary: {
      backgroundColor: theme.palette.grey[theme.palette.type === 'light' ? 200 : 700],
    },
    bar: {
      borderRadius: 5,
      backgroundColor: '#FFDD52',
    },
  }))(LinearProgress);

const useStyles = makeStyles((theme)=>({
    gridContainer: {
      paddingLeft: "20px",
      paddingRight: "20px",
      background:"#7555D9",
      minHeight:"100vh"
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
      },
      pro:{
          textAlign:"center"
      }

  }));
function PlayQuiz({quizlist,quizId,createdBy}) {
  console.log(quizId)
 const[score,setScore]=useState(0)
 const [activeStep, setActiveStep] = React.useState(0);
 const [meter,setMeter]=useState(false);
 const [correctAns,setCorrectAns]= useState(0);
 const [clickedAns,setClickedAns]= useState(0);
 const [progress,setProgress]=useState(false);
 const [count,setCount]=useState(1);
 const json =localStorage.getItem("feedbackby")

  let quiz = [
    {
        label: quizlist?.question,
        options: quizlist?.options,
    },
];

    const classes = useStyles();
    if (quizlist?.length) {
      quiz = quizlist;
    }

    function showProgress(index) {

      var increment = Math.ceil((activeStep) / (maxSteps) * 100);

      setProgress(increment)

    }
    const maxSteps = quiz.length;
    const handleSelect = (optId,label)=>{
       setClickedAns(label)
       if(label===quiz[activeStep].answer){
            increaseScore();
           setCorrectAns(quiz[activeStep].answer)


       }
       else{
         setCorrectAns(quiz[activeStep].answer)
       }

    }


const handleNext = (optId,label)=>{

  if(maxSteps-1 !==activeStep){
    setTimeout(()=>{setActiveStep(prevActiveStep => prevActiveStep + 1)
      setCount(count+1)},500)


  }
  else{

     handleSubmit()
  }
  handleSelect(optId,label);

  showProgress(quiz[activeStep].qusId);
}
const increaseScore = ()=>{
  setScore(score+1)
}
const handleSubmit = async() =>{
    const data={
      quizId:quizId,
      feedbackBy:json,
      score:score
    }
    const options = {
      method: 'POST',
      headers: {
        "Apiuserid": "49507884",
        "Scope": "In.QuizApp.INSERT",
      }
    }
    const result = await services.getResult(data, options)
     setMeter(result.data.data)
}
if(meter===true)
{
  var datalist={list:quizlist.length,quizId:quizId,createdBy:createdBy,score:{score}}
  localStorage.setItem('alldata',JSON.stringify(datalist))
  return(<Redirect to="/score" push={true} />)

}
    return (
        <div className={classes.gridContainer}>
        <Grid container>
           <Grid item xs></Grid>
            <Grid item xs={11} sm={6} md={6} lg={4} >
                 <div justify="center">
                    <h3 className="text1">2021 Friendship</h3>
                    <h3 className="text2">Dare</h3>
                 </div>
                 <div justify="center">     
                     <h4 className="question">Question <span>{count}/{maxSteps}</span></h4>
                 </div>
                 <div justify="center">
                    <BorderLinearProgress variant="determinate" value={progress} />
                 </div>
                 <Card className="playoptioncard">
                <h3 className="questionname">{count}:{quiz[activeStep].title}</h3>
                   <div className="playcard">
                    {
                    quiz[activeStep]?.options?.map((options,index)=>{

                    return(
                        <Grid
                      container
                      className= {
                        correctAns === options.optionLabel ?
                        'correctplay' : 
                      clickedAns === options.optionLabel?
                      'incorrectplay' : 'options'}
                      correct={quiz[activeStep].answer}
                      key={index}
                      onClick={()=>{handleNext(options.optionId,options.optionLabel)}}
                      >
                      <Grid item xs={4} sm={4} md={4}lg={4}>
                      {options.optionImg.length
                                ?<img className="playImg" 
                                  src={options.optionImg}/>
                                :<span></span>
                              }
                      </Grid>
                      <Grid item xs={8} sm={8} md={8}lg={8} className="optionbg" >
                        <CardContent className="playcontent">
                          <h3 className="play-head"id="cardText">
                        {options.optionLabel}
                          </h3>
                        </CardContent>
                      </Grid>
                      </Grid>
                    )

                    })
                    } 
                </div>
         </Card>
            </Grid>
            <Grid item xs></Grid>
       </Grid>
       <Footer/>
  </div>
    )
}

export default PlayQuiz
